# Testimport randomdef greeting():    print('\n' * 50)    print("Welcome to our Hangman game!\nYou will be playing against a computer.\n\n")def dic_choice():    try:        choice = int(input(            "Would you like to play\n 1. Locations\n 2. Movies\n 3. Video Games\n 4. Famous Companies\n\nType 1  2  3  "            "4: "))    except:        print('Sorry please type a number from 1 to 4')    else:        return choicedef print_dashes(dictionary, location, movie, video_game, famous_company, rand_int, guessed_letter, missed_let,                 dic_word):    if dictionary == 1:        dic_word = location[rand_int]        if guessed_letter not in dic_word:            missed_let.append(guessed_letter)        print(dic_word)        for char in dic_word:            if char == guessed_letter.lower():                print(guessed_letter.lower() + ' ', sep=' ', end='', flush=True)            elif char == guessed_letter.upper():                print(guessed_letter.upper() + ' ', sep=' ', end='', flush=True)            elif char == ' ':                print(' ', sep=' ', end='', flush=True)            else:                print('_ ', sep=' ', end='', flush=True)        return len(dic_word)    if dictionary == 2:        if guessed_letter not in movie[rand_int]:            missed_let.append(guessed_letter)        for char in movie[rand_int]:            if char == guessed_letter.lower():                print(guessed_letter.lower() + ' ', sep=' ', end='', flush=True)            elif char == guessed_letter.upper():                print(guessed_letter.upper() + ' ', sep=' ', end='', flush=True)            elif char == ' ':                print(' ', sep=' ', end='', flush=True)            else:                print('_ ', sep=' ', end='', flush=True)        return len(movie[rand_int])    if dictionary == 3:        if guessed_letter not in video_game[rand_int]:            missed_let.append(guessed_letter)       